var vendas=[],totalVendas=0;function listarVendas(){var resposta="=>";for(i=0;i<vendas.length;i++)resposta=(resposta=i%3==0?resposta.concat("<br>"):resposta).concat(vendas[i],", ");document.getElementById("resposta").innerHTML="Relação de Vendas: (Preço kg/ Consumo/ Valor da venda) "+resposta+"<br>Valor total das vendas: R$"+totalVendas}var listar=document.getElementById("listar");function gravarVendas(preco_quilo,consumo_cliente,valorPedido){return vendas.push(preco_quilo,consumo_cliente,valorPedido),vendas}function preco(){var calculo,preco_quilo=document.getElementById("buffet").value,consumo_cliente=document.getElementById("consumo").value;(document.getElementById("resposta").textContent="")!=preco_quilo&&""!=consumo_cliente?(totalVendas+=calculo=consumo_cliente*preco_quilo/1e3,gravarVendas(preco_quilo,consumo_cliente,calculo),document.getElementById("resposta").innerHTML="O valor a ser pago é igual a R$"+calculo):alert("Preencha todos os campos!")}listar.addEventListener("click",listarVendas);var mostrar=document.getElementById("calcular");function limparCampos(){document.getElementById("buffet").value="",document.getElementById("consumo").value="",document.getElementById("resposta").textContent="",document.getElementById("buffet").focus()}mostrar.addEventListener("click",preco);var Limpar=document.getElementById("limpar");Limpar.addEventListener("click",limparCampos);
