function calculaIdade(){var Pessoa={nome:document.getElementById("nome").value,cpf:document.getElementById("cpf").value,dataNascimento:document.getElementById("data").value};document.getElementById("resposta").textContent="";var mesNasc=new Date,anoAtual=mesNasc.getFullYear(),mesAtual=mesNasc.getMonth()+1,idade=Pessoa.dataNascimento.split("/"),diaNasc=idade[0],mesNasc=idade[1],idade=anoAtual-idade[2];""==Pessoa.dataNascimento?(alert("Digite uma data de nascimento!"),document.getElementById("data").focus):((mesAtual<mesNasc||mesAtual==mesNasc&&(new Date).getDate()<diaNasc)&&idade--,document.getElementById("resposta").innerHTML="PESSOA CADASTRADA: <br>Nome: "+Pessoa.nome+"<br>CPF: "+Pessoa.cpf+"<br>Data de Nascimento: "+Pessoa.dataNascimento+"<br>Idade: "+idade)}var cadastrarPessoa=document.getElementById("cadastrar");cadastrarPessoa.addEventListener("click",calculaIdade);